package com.parita.bookstore.service;

import com.parita.bookstore.model.Author;
import org.springframework.stereotype.Service;

import java.util.ArrayList;
import java.util.List;

@Service
public class HomeService {


    public List<Author> getAuthors(){

        List<Author> authors = new ArrayList<>();

        Author author1 = new Author(1, "data:image/jpeg;base64,",
                "Test 1","This is Test 1");

        Author author2 = new Author(2, "data:image/jpeg;base64,",
                "Test 2", "This is Test 2");

        authors.add(author1);
        authors.add(author2);

        return authors;
    }



    public List<Author> searchedAuthors(String authorName){

        List<Author> authors = new ArrayList<>();

        Author author1 = new Author(1, "data:image/jpeg;base64,",
                authorName,"This is Test 1");

        authors.add(author1);


        return authors;
    }
}
